package Bucles;

public class MúltiplosDe7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("!BIENVENIDO AL PROGRAMA DE LOS MÚLTIPLOS DE 7 MENORES A 1000¡" + "\n");

		for(int i = 7; i < 1000; i += 7) {
			System.out.print(i + ", ");
		}
	}
}
