
package Condicionales;

public class If_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 0;
		a = 10;
		
		if(a == 10) {
			System.out.println("if_1");
		}
		else {
			System.out.println("else_1");
		}
		System.out.println("Variable a= " + a);
	}

}
